mapper.update:
update blog_inform
set in_content = #{inContent,jdbcType=VARCHAR},
  in_informtime = #{inInformtime,jdbcType=TIMESTAMP},
  in_user_id = #{inUserId,jdbcType=INTEGER}
where in_id = #{inId,jdbcType=INTEGER}

mapper.select:
select in_id, in_content, in_informtime, in_user_id
from blog_inform
where in_id = #{inId,jdbcType=INTEGER}
